CREATE VIEW Est08_Bloqueio_TermoSaldo AS

SELECT 
	T.TermoUsuarioEstCod AS 'TermoEstCod',
	T.TermoUsuarioCod AS 'TermoLogin',
	T.TermoTipo AS 'TermoTipo',
	T.TermoUsuarioAceito AS 'TermoAceito',
	CASE WHEN T.TermoUsuarioAceito = 0 THEN 'S' ELSE 'N' END AS 'TermoBloqueio',
	CASE WHEN T.TermoUsuarioAceito = 0 THEN 'Termo de cessão ainda não foi aceito.' ELSE '' END AS 'TermoBloqueioMotivo'
	
FROM TermoUsuario T;

